import React, { useRef, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Image, Form, Card, Row, Col, Input, Button } from 'antd';
import { useNavigate } from 'react-router-dom';
import { getSignedRequest } from '../../actions/upload.action';
import './index.css';

const FormCategory = ({ cardTitle, okText, label, category, backURL, handleAction }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const formRef = useRef();

  const upload = useSelector((state) => state.upload);
  const { imageUrl } = upload;

  const onChange = (e) => {
    let files = e.target.files;
    dispatch(getSignedRequest(files[0]));
  };

  const onFinish = (values) => {
    values.id = category?.id;
    values.image = imageUrl ? imageUrl : category ? category.image : '';
    dispatch(handleAction(values));
  };

  useEffect(() => {
    formRef.current?.setFieldsValue({
      name: category?.name,
      image: category?.image
    });
  }, [category]);
  return (
    <Card loading={false} className="category-card">
      <h2 className="page-title">{cardTitle}</h2>
      <Row justify="space-around">
        <Col span={3}>
          <Image
            src={imageUrl ? imageUrl : category ? category.image : ''}
            className="preview-category-image"
            fallback="data:image/png;base64,"
          />
          <input
            type="file"
            class="custom-file-input custom-file-input--category"
            name="image-file"
            onChange={(e) => onChange(e)}
          />
        </Col>
        <Col span={12}>
          <Form
            ref={formRef}
            className="category-form"
            labelCol={{ span: 8 }}
            wrapperCol={{ span: 16 }}
            name="product-form"
            onFinish={onFinish}>
            <Form.Item
              label={label}
              name="name"
              rules={[
                {
                  required: true,
                  message: 'Vui lòng nhập tên vắc xin!',
                  whitespace: true
                }
              ]}>
              <Input />
            </Form.Item>
            <Form.Item label="Ảnh" name="image" style={{ display: 'none' }}>
              <Input />
            </Form.Item>
            <Row justify="center">
              <Button
                type="primary"
                className="btn-cancel"
                onClick={() => {
                  navigate(backURL);
                }}>
                Hủy
              </Button>
              <Button
                type="primary"
                htmlType="submit"
                className="btn-add"
                style={{ background: '#198754', border: '#198754' }}>
                {okText}
              </Button>
            </Row>
          </Form>
        </Col>
      </Row>
    </Card>
  );
};

export default FormCategory;
