import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { Image } from 'antd';
import Loader from '../../components/Loader';
import Message from '../../components/Message';
import Header from '../../components/table/Header';
import useDataTable from '../../components/table/DataTable';
import {
  getCategoryList,
  deleteCategory,
  deleteMultiCategory
} from '../../actions/category.action';
import './index.css';

const AdminDiseaseCategories = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [open, setOpen] = useState(false);

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const categoryList = useSelector((state) => state.categoryList);
  const { loading, error, categories, totalItem } = categoryList;

  const categoryDelete = useSelector((state) => state.categoryDelete);
  const { deleteSuccess } = categoryDelete;

  const multiCategoryDelete = useSelector((state) => state.multiCategoryDelete);
  const { deleteMultiSuccess } = multiCategoryDelete;

  const upload = useSelector((state) => state.upload);
  const { imageUrl } = upload;

  const showModal = () => {
    setOpen(true);
  };
  const hideModal = () => {
    setOpen(false);
  };

  const handleDeleteSingleCategory = (id) => {
    dispatch(deleteCategory(id));
  };

  const handleDeleteMultiVaccine = (ids) => {
    dispatch(deleteMultiCategory(ids));
  };

  const getCategories = (page) => {
    dispatch(getCategoryList({ perPage: 10, page: page }));
  };

  const handleOnSearch = (name) => {
    dispatch(getCategoryList({ name, perPage: 10 }));
  };

  const handleOk = () => {
    hideModal();
  };

  const columns = [
    {
      title: '#',
      dataIndex: 'index',
      key: 'key'
    },
    {
      title: 'Ảnh',
      dataIndex: 'image',
      key: 'image',
      render: (theImageURL) => (
        <Image
          className="categories__image"
          src={theImageURL}
          fallback="data:image/png;base64,"
        />
      )
    },
    {
      title: 'Phòng bệnh',
      dataIndex: 'name',
      key: 'name'
    }
  ];

  const data = {};
  data.totalElements = totalItem;
  data.content = categories?.map((category, index) => ({
    key: category.id,
    index: index + 1,
    image: category.image,
    name: category.name
  }));

  const {
    DataTable,
    hasSelected,
    selectedRowKeys,
    selectedRow,
    currentPage,
    pageSize,
    resetPagination
  } = useDataTable({
    columns: columns,
    dataSource: data,
    updateEntityPath: 'admin-home/disease-categories/update',
    handleDelete: handleDeleteSingleCategory,
    handleChangePage: getCategories
  });

  useEffect(() => {
    if (userInfo && userInfo.user.roles.includes('admin')) {
      dispatch(getCategoryList({ perPage: 10 }));
    } else {
      navigate('/login');
    }
  }, [userInfo, deleteSuccess, deleteMultiSuccess]);

  return loading ? (
    <Loader />
  ) : error ? (
    <Message description={error} />
  ) : (
    <>
      <div className="categories-card">
        <>
          <Header
            addNewPath="admin-home/disease-categories/add"
            selectedRowKeys={selectedRowKeys}
            hasSelected={hasSelected}
            handleMultiDelete={handleDeleteMultiVaccine}
            handleSearch={handleOnSearch}
          />
          <DataTable />
        </>
      </div>
    </>
  );
};

export default AdminDiseaseCategories;
