import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Container from '../../layout/Container';
import { LikeOutlined, ArrowDownOutlined, ArrowUpOutlined } from '@ant-design/icons';
import { Col, Row, Statistic, Card, Divider, Table, Image } from 'antd';
import { useNavigate, Link } from 'react-router-dom';
import Loader from '../../components/Loader';
import Message from '../../components/Message';
import { getVaccineList } from '../../actions/vaccine.action';
import './index.css';

const DashboardPage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const vaccineList = useSelector((state) => state.vaccineList);
  const { loading, error, vaccines, totalItem } = vaccineList;

  useEffect(() => {
    if (userInfo && userInfo.user.roles.includes('admin')) {
      dispatch(getVaccineList({ perPage: 10 }));
    } else {
      navigate('/login');
    }
  }, [userInfo]);
  const columns = [
    {
      title: '#',
      dataIndex: 'index',
      key: 'key'
    },
    {
      title: 'Ảnh',
      dataIndex: 'image',
      key: 'image',
      render: (theImageURL) => (
        <Image
          className="vaccine-list__image"
          src={theImageURL ? theImageURL : ''}
          fallback="data:image/png;base64,"
        />
      )
    },
    {
      title: 'Mã vắc xin',
      key: 'vaccineCode',
      dataIndex: 'vaccineCode'
    },
    {
      title: 'Tên vắc xin',
      dataIndex: 'name',
      key: 'name'
    },

    {
      title: 'Số lượng đã tiêm',
      dataIndex: 'price',
      key: 'price'
    }
  ];

  const data = {};
  data.totalElements = totalItem;
  data.content = vaccines.map((vaccine, index) => ({
    key: vaccine.id,
    index: index + 1,
    image: vaccine.image,
    name: vaccine.name,
    price: vaccine.price,
    vaccineCode: vaccine.vaccineCode,
    origin: vaccine.origin,
    qty: vaccine.quantity
  }));

  return (
    <div>
      <Container>
        <Row gutter={16}>
          <Col span={6}>
            <Card>
              <Statistic title="Người đăng kí / Lượt khám tối đa" value={93} suffix="/ 100" />
            </Card>
          </Col>
          <Col span={6}>
            <Card>
              <Statistic title="Người check in/ Lượt khám tối đa" value={93} suffix="/ 100" />
            </Card>
          </Col>

          <Col span={6}>
            <Card>
              <Statistic
                title="Active"
                value={11.28}
                precision={2}
                valueStyle={{
                  color: '#3f8600'
                }}
                prefix={<ArrowUpOutlined />}
                suffix="%"
              />
            </Card>
          </Col>
          <Col span={6}>
            <Card>
              <Statistic
                title="Idle"
                value={9.3}
                precision={2}
                valueStyle={{
                  color: '#cf1322'
                }}
                prefix={<ArrowDownOutlined />}
                suffix="%"
              />
            </Card>
          </Col>
        </Row>
        <br></br>
        <Row>
          <Col span={24}>
            <Card className="top-vaccines">
              <h3 className="top-vaccines__title">Top 10 vắc xin được tiêm nhiều nhất</h3>
              <Divider />
              {loading ? (
                <Loader />
              ) : error ? (
                <Message description={error} />
              ) : (
                <Table
                  style={{ marginTop: 20 }}
                  rowKey={(record) => record.key}
                  dataSource={data.content}
                  columns={columns}
                  pagination={false}
                />
              )}
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default DashboardPage;
